word, get_input, wordList, tries = __import__("random").choice([i.strip() for i in open("wordlist.txt")]), (lambda inp="": inp if len(inp)==5 and inp.lower() in wordList else get_input(inp = input("Enter a guess: "))), [i.strip() for i in open("wordlist.txt")], 0
